mapper.update:

update SALES
set userID = #{userid,jdbcType=BIGINT},
  bondID = #{bondid,jdbcType=BIGINT},
  price = #{price,jdbcType=BIGINT},
  `date` = #{date,jdbcType=TIMESTAMP}
where sellID = #{sellid,jdbcType=BIGINT}

mapper.select:

select sellID, userID, bondID, price, `date`
from SALES
where sellID = #{sellid,jdbcType=BIGINT}

select sellID, userID, bondID, price, `date`
from SALES

      date between #{startDate,jdbcType=DATE} and #{endDate,jdbcType=Date} and

  bondname = #{bondname,jdbcType=String}

and

  username = #{username,jdbcType=String}

      date between #{startDate,jdbcType=DATE} and #{endDate,jdbcType=DATE}

select sl.*, u.userName, b.bondName from sales sl
LEFT JOIN `user` u ON sl.userID = u.userId
LEFT JOIN bond b on sl.bondID = b.bondID WHERE 1 = 1

  and userName = #{userName}


  and bondName = #{bondName}


  and price = #{price}


  and DATE_FORMAT(sl.date, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d')
